  {
    "idNpc": 0,
    "text": "Hello?"
  },
  {
    "idNpc": 0,
    "text": "I just arrived"
  },
  {
    "idNpc": 0,
    "text": "Something has gone wrong"
  },
  {
    "idNpc": 1,
    "text": "Inform"
